  '2xs': 'w-3 h-3',
  xs: 'w-3.5 h-3.5',
  sm: 'w-4 h-4',
  md: 'w-5 h-5',
  lg: 'w-5 h-5',
  xl: 'w-6 h-6',
  '2xl': 'w-8 h-8'
}

const sizeClass = computed(() => {
  if (props.size && sizeMap[props.size]) {
    return sizeMap[props.size]
  }
  if (!props.size) {
    return 'w-5 h-5'
  }
  return ''
})

const customSizeStyle = computed(() => {
  if (props.size && !sizeMap[props.size]) {
